MxM = {}MxM.S_projectCallback = {}MxM.C_projectCallback = {} MxM.ProjectId = 0 RegisterNetEvent('MxM:T_C_projectCallback') AddEventHandler('MxM:T_C_projectCallback', function(name, requestId, ...)	local playerId = source  	MxM.T_C_projectCallback(name, requestID, playerId, function(...)		TriggerServerEvent('MxM:clientCallback', requestId, ...)	end, ...)end)MxM.T_S_projectCallback = function(name, PJ, ...)	MxM.S_projectCallback[MxM.ProjectId] = PJ	TriggerServerEvent('MxM:T_S_projectCallback', name, MxM.ProjectId, ...)	if MxM.ProjectId < 99999 then		MxM.ProjectId = MxM.ProjectId + 1	else		MxM.ProjectId = 0	end end MxM.R_C_projectCallback = function(name, PJ)	MxM.C_projectCallback[name] = PJ end MxM.T_C_projectCallback = function(name, requestId, source, PJ, ...)	if MxM.C_projectCallback[name] ~= nil then		MxM.C_projectCallback[name](PJ, ...)	end end RegisterNetEvent('MxM:serverCallback') AddEventHandler('MxM:serverCallback', function(requestId, ...)	MxM.S_projectCallback[requestId](...)	MxM.S_projectCallback[requestId] = nil end)


