MxM = {} MxM.S_projectCallback = {} MxM.C_projectCallback = {} MxM.ProjectId = 0 MxM.R_S_projectCallback = function(name, PJ)	MxM.S_projectCallback[name] = PJ end MxM.T_S_projectCallback = function(name, requestId, source, PJ, ...)	if MxM.S_projectCallback[name] ~= nil then		MxM.S_projectCallback[name](source, PJ, ...)	end end MxM.T_C_projectCallback = function(source, name, PJ, ...)	MxM.C_projectCallback[MxM.ProjectId] = PJ	TriggerClientEvent('MxM:T_C_projectCallback', source, name, MxM.ProjectId, ...)	if MxM.ProjectId < 99999 then		MxM.ProjectId = MxM.ProjectId + 1	else		MxM.ProjectId = 0	end end RegisterServerEvent('MxM:clientCallback')AddEventHandler('MxM:clientCallback', function(requestId, ...)	MxM.C_projectCallback[requestId](...)	MxM.C_projectCallback[requestId] = nil end) RegisterServerEvent('MxM:T_S_projectCallback') AddEventHandler('MxM:T_S_projectCallback', function(name, requestId, ...)	local _source = source	MxM.T_S_projectCallback(name, requestID, _source, function(...)		TriggerClientEvent('MxM:serverCallback', _source, requestId, ...)	end, ...)end)

